import { useState } from 'react';
import { Events, Connections } from './data';

import SettingsIcon from '@mui/icons-material/Settings';

export default function Main() {
    const [showManageTimeLine, setShowManageTimeline] = useState(false);

    const [selectedStep, setSelectedStep] = useState<number | null>(null);

    const maxStepId = Events.length > 0
        ? Math.max(...Events.map(event => event.stepId))
        : 0;

    console.log(maxStepId);

    return (
        <>
            <div className="container-fluid">
                <div className="row">
                    <div className="col-12 p-1">
                        <button className="btn btn-primary" onClick={() => setShowManageTimeline(true)}><SettingsIcon /> Manage timeline</button>
                    </div>
                </div>
            </div>

            {showManageTimeLine && (
                <div className="modal d-block" tabIndex={-1} role="dialog" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Manage your timeline</h5>
                                <button type="button" className="btn-close" onClick={() => setShowManageTimeline(false)}></button>
                            </div>
                            <div className="modal-body">
                                <div className="mb-3 d-flex flex-column">
                                    <div className="form-floating">
                                        <select className="form-select" id="select-step" value={selectedStep ?? ''} onChange={(e) => setSelectedStep(Number(e.target.value))}>
                                            <option selected>Step</option>
                                            {

                                            }
                                        </select>
                                        <label htmlFor="select-step">Select the step to modify</label>
                                    </div>
                                    {/* <select id="select-step" onChange={(e) => setSelectedStep(Number(e.target.value))}>
                                    {Steps.map((step) => (
                                        <option key={step.id} value={step.id}>Step {step.id}</option>
                                    ))}
                                </select> */}
                                </div>
                            </div>
                            <div className="modal-footer">
                                <button className="btn btn-primary">
                                    Save
                                </button>
                                <button className="btn btn-danger">
                                    Cancel
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </>
    )
}
